item.useInMap = function( self, map, x, y, xd, yd, hotbar )	local xd, yd = x * map.blockSize - game.player.x, y * map.blockSize - game.player.y	local p = game.resource.entity.newProjectile( xd, yd, 32, 32 )	p:newAnimation( "Normal" )	p:setAnimation( "Normal" )	p:newFrame( game.data.Items[self.name].Bullet )	p:move( "set", game.player.x, game.player.y )	game.world:getMapByID( ):newEntity( p )end