local shotTime = 0item.useInMap = function( self, map, x, y, xd, yd, hotbar )	if love.timer.getTime( ) - 0.5 > shotTime then		local xd, yd = x - ( game.player.x + game.player.w / 2 ), y - ( game.player.y + game.player.w / 2 )		game.renderdata = xd..", "..yd		local angle = math.atan2( xd, yd ) * 180 / math.pi		local p = game.resource.entity.newProjectile( angle, 20, 8, 8 )		p:newAnimation( "Normal" )		p:setAnimation( "Normal" )		p:newFrame( game.data.Items[self.name].Bullet )		p:move( "set", game.player.x, game.player.y )		game.world:getMapByID( ):newEntity( p )		shotTime = love.timer.getTime( )	endend